// SPDX-License-Identifier: BSD 3-Clause License
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/IAccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract BLXToken is ERC20, AccessControl {
    uint256 private immutable _cap;
    //admin minter 0x00000000000000000000000000000041444d494e5f4d494e5445525f524f4c45
    bytes32 public constant ADMIN_MINTER_ROLE = keccak256("ADMIN_MINTER_ROLE");
    //minter 0x0000000000000000000000000000000000000000004d494e5445525f524f4c45
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    //admin burner 0x00000000000000000000000000000041444d494e5f4255524e45525f524f4c45
    bytes32 public constant ADMIN_BURNER_ROLE = keccak256("ADMIN_BURNER_ROLE");
    //burner 0x0000000000000000000000000000000000000000004255524e45525f524f4c45
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    constructor(
        string memory name,
        string memory symbol,
        uint256 cap_
    ) ERC20(name, symbol) {
        require(cap_ > 0, "ERC20Capped: cap is 0");
        _cap = cap_;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setRoleAdmin(
            0x00000000000000000000000000000041444d494e5f4d494e5445525f524f4c45,
            DEFAULT_ADMIN_ROLE
        ); //deployer can set admin minter role
        _setRoleAdmin(
            0x00000000000000000000000000000041444d494e5f4255524e45525f524f4c45,
            DEFAULT_ADMIN_ROLE
        ); //deployer can set admin burner role
        _setRoleAdmin(
            0x0000000000000000000000000000000000000000004d494e5445525f524f4c45,
            0x00000000000000000000000000000041444d494e5f4d494e5445525f524f4c45
        ); // admin minter can set minter role
        _setRoleAdmin(
            0x0000000000000000000000000000000000000000004255524e45525f524f4c45,
            0x00000000000000000000000000000041444d494e5f4255524e45525f524f4c45
        ); // admin burner can set burner role
    }

    function mint(address to, uint256 amount)
        public
        onlyRole(
            0x0000000000000000000000000000000000000000004d494e5445525f524f4c45
        )
    {
        //only minter
        require(
            ERC20.totalSupply() + amount <= cap(),
            "ERC20Capped: cap exceeded"
        );
        _mint(to, amount);
    }

    function burn(uint256 amount)
        public
        onlyRole(
            0x0000000000000000000000000000000000000000004255524e45525f524f4c45
        )
    {
        _burn(msg.sender, amount);
    }

    function cap() public view virtual returns (uint256) {
        return _cap;
    }
}
